    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}

{% endblock %}

{% block body %}
<div>
    <h1>Votre panier</h1>
</div>

{% if items | length > 0 %}
    <table class=\"table\">
    <thead>
        <tr>
            <th>Produit</th>
            <th>Prix</th>
            <th>Quantit√©</th>
            <th>Total</th>
            <th></th>
        </tr>
    </thead>
    <tbody>
        {% for item in items %}
            <tr>
                <td>{{ item.product.title }}</td>
                <td>{{ item.product.price }}</td>
                <td>{{ item.quantity }}</td>
                <td>{{ item.product.price * item.quantity }}</td>
                <td>
                    <a href=\"{{ path('cart_remove', {'id': item.product.id}) }}\" class=\"btn btn-danger btn-sm\">
                     Supprimer
                    </a>
                </td>
            </tr>
        {% endfor %}
    </tbody>
    <tfoot>
        <tr>
            <td colspan=\"3\" class=\"text-right\">Total :
            </td>
            <td>{{ total }}</td>
            <td></td>
        </tr>
    </tfoot>
</table>
{% else %}
<p>Surprise, c'est vide !</p>

{% endif %}





{% endblock %}
